import React, { useState } from 'react';
import meter1 from "../assets/img/meter1.svg";
import meter2 from "../assets/img/meter2.svg";
import meter3 from "../assets/img/meter3.svg";
import Carousel from 'react-multi-carousel';
import 'react-multi-carousel/lib/styles.css';
import colorSharp from "../assets/img/color-sharp.png";

export const Skills = () => {
  const [hoveredSkill, setHoveredSkill] = useState(null);

  const skillDetails = {
    "Python Scripting": "Expert in writing Python scripts for automation, data processing, and system integration.",
    "ML Frameworks": "Proficient in machine learning frameworks like TensorFlow and PyTorch for model development.",
    "Data Analysis": "Experienced in analyzing data using statistical methods and data visualization tools.",
    "Databases": "Skilled in SQL, database design, and optimization for efficient data storage and retrieval.",
    "DevOps": "Expertise in setting up CI/CD pipelines and managing deployments using tools like Jenkins and GitHub Actions."
  };

  const responsive = {
    superLargeDesktop: {
      breakpoint: { max: 4000, min: 3000 },
      items: 5
    },
    desktop: {
      breakpoint: { max: 3000, min: 1024 },
      items: 3
    },
    tablet: {
      breakpoint: { max: 1024, min: 464 },
      items: 2
    },
    mobile: {
      breakpoint: { max: 464, min: 0 },
      items: 1
    }
  };

  return (
    <section className="skill" id="skills">
        <div className="container">
            <div className="row">
                <div className="col-12">
                    <div className="skill-bx wow zoomIn">
                        <h2>Skills</h2>
                        <p>Acquired skills through academic and professional training include: <br></br></p>
                        <Carousel responsive={responsive} infinite={true} className="owl-carousel owl-theme skill-slider">
                            {Object.keys(skillDetails).map((skill) => (
                              <div
                                className="item"
                                key={skill}
                                onMouseEnter={() => setHoveredSkill(skill)}
                                onMouseLeave={() => setHoveredSkill(null)}
                              >
                                <img src={meter1} alt="Image" />
                                <h5>{skill}</h5>
                                {hoveredSkill === skill && (
                                  <div className="skill-tooltip">
                                    {skillDetails[skill]}
                                  </div>
                                )}
                              </div>
                            ))}
                        </Carousel>
                    </div>
                </div>
            </div>
        </div>
        <img className="background-image-left" src={colorSharp} alt="Image" />
    </section>
  )
}
