import { Container, Row, Col, Tab, Nav } from "react-bootstrap";
import { ProjectCard } from "./ProjectCard";
import projImg1 from "../assets/img/ksu.jpg";
import projImg2 from "../assets/img/aui.jpg";
import projImg3 from "../assets/img/lexis."; // Example professional image
import projImg4 from "../assets/img/ETX-Capital-Lgo.png"; // Example professional image
import projImg5 from "../assets/img/acatium.png"; // Example professional image
import projImg6 from "../assets/img/skillademia.png"; // Example professional image

import colorSharp2 from "../assets/img/color-sharp2.png";
import 'animate.css';
import TrackVisibility from 'react-on-screen';

export const Experiences = () => {

  const academicExperiences = [
    {
      title: "Kennesaw State University",
      description: "2nd Year PhD Student",
      imgUrl: projImg1,
    },
    {
      title: "Al Akhawayn University",
      description: "Bachelor of Science in Computer Science",
      imgUrl: projImg2,
    },
  ];

  const professionalExperiences = [
    {
      title: "Tech Company",
      description: "Software Engineer",
      imgUrl: projImg3,
    },
    {
      title: "Startup",
      description: "Lead Developer",
      imgUrl: projImg4,
    },
    {
      title: "Startup",
      description: "Lead Developer",
      imgUrl: projImg5,
    },
      {
      title: "Startup",
      description: "Lead Developer",
      imgUrl: projImg6,
    },
  ];

  return (
    <section className="project" id="background">
      <Container>
        <Row>
          <Col size={12}>
            <TrackVisibility>
              {({ isVisible }) =>
              <div className={isVisible ? "animate__animated animate__fadeIn": ""}>
                <h2>Background</h2>
                <p>A committed team player who values the gratification of success as well as the challenging journey to it. Nevertheless, I do enjoy improving and acquiring skills that are thought to be vital in the industry my work revolves around.</p>
                <Tab.Container id="projects-tabs" defaultActiveKey="first">
                  <Nav variant="pills" className="nav-pills mb-5 justify-content-center align-items-center" id="pills-tab">
                    <Nav.Item>
                      <Nav.Link eventKey="first">Academic</Nav.Link>
                    </Nav.Item>
                    <Nav.Item>
                      <Nav.Link eventKey="second">Professional</Nav.Link>
                    </Nav.Item>
                    <Nav.Item>
                      <Nav.Link eventKey="third">Request CV</Nav.Link>
                    </Nav.Item>
                  </Nav>
                  <Tab.Content id="slideInUp" className={isVisible ? "animate__animated animate__slideInUp" : ""}>
                    <Tab.Pane eventKey="first">
                      <Row>
                        {
                          academicExperiences.map((experience, index) => {
                            return (
                              <ProjectCard
                                key={index}
                                {...experience}
                                />
                            )
                          })
                        }
                      </Row>
                    </Tab.Pane>
                    <Tab.Pane eventKey="second">
                      <Row>
                        {
                          professionalExperiences.map((experience, index) => {
                            return (
                              <ProjectCard
                                key={index}
                                {...experience}
                                />
                            )
                          })
                        }
                      </Row>
                    </Tab.Pane>
                    <Tab.Pane eventKey="third">
                      <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Cumque quam, quod neque provident velit, rem explicabo excepturi id illo molestiae blanditiis, eligendi dicta officiis asperiores delectus quasi inventore debitis quo.</p>
                    </Tab.Pane>
                  </Tab.Content>
                </Tab.Container>
              </div>}
            </TrackVisibility>
          </Col>
        </Row>
      </Container>
      <img className="background-image-right" src={colorSharp2}></img>
    </section>
  )
}
