import React, { useEffect, useRef } from 'react';
import { Chart } from 'chart.js';
import colorSharp from "../assets/img/color-sharp.png";

export const Skills = () => {
  const chartRef = useRef(null);

  useEffect(() => {
    const ctx = chartRef.current.getContext("2d");

    const data = {
      labels: ['Python Scripting', 'ML Frameworks', 'Databases', 'Data Analysis', 'DevOps'],
      datasets: [{
        label: 'Skill Proficiency',
        data: [8, 7, 9, 8, 6], // Assuming a scale of 1-10 for skill levels
        backgroundColor: 'rgba(54, 162, 235, 0.2)',
        borderColor: 'rgba(54, 162, 235, 1)',
        pointBackgroundColor: 'rgba(54, 162, 235, 1)',
        pointBorderColor: '#fff',
        pointHoverBackgroundColor: '#fff',
        pointHoverBorderColor: 'rgba(54, 162, 235, 1)',
        fill: true,
      }]
    };

    const config = {
      type: 'radar',
      data: data,
      options: {
        scales: {
          r: {
            angleLines: {
              display: true
            },
            suggestedMin: 0,
            suggestedMax: 10,
            ticks: {
              stepSize: 2
            }
          }
        },
        plugins: {
          tooltip: {
            callbacks: {
              label: function(tooltipItem) {
                const index = tooltipItem.dataIndex;
                return `${data.labels[index]}: ${tooltipItem.raw} - ${skills[index].details}`;
              }
            }
          }
        }
      },
    };

    const myChart = new Chart(ctx, config);

    return () => {
      myChart.destroy();
    };
  }, []);

  return (
    <section className="skill" id="skills">
        <div className="container">
            <div className="row">
                <div className="col-12">
                    <div className="skill-bx wow zoomIn">
                        <h2>Skills</h2>
                        <p>Acquired skills through academic and professional training include:</p>
                        <canvas ref={chartRef} width="400" height="400"></canvas>
                    </div>
                </div>
            </div>
        </div>
        <img className="background-image-left" src={colorSharp} alt="Background"/>
    </section>
  );
}
