import { Container, Row, Col, Carousel } from "react-bootstrap";
import { ProjectCard } from "./ProjectCard";
import projImg1 from "../assets/img/ksu.jpg";
import projImg2 from "../assets/img/aui.jpg";
import projImg3 from "../assets/img/Lexis.png"; // Example professional image
import projImg4 from "../assets/img/etx.png"; // Example professional image
import projImg5 from "../assets/img/acatium.png"; // Example professional image
import projImg6 from "../assets/img/skillademia.png";
import projImg7 from "../assets/img/upswing.png";
import projImg8 from "../assets/img/ksu lab.png";
import colorSharp2 from "../assets/img/color-sharp2.png";
import 'animate.css';
import TrackVisibility from 'react-on-screen';

export const Experiences = () => {

  const academicExperiences = [
    {
      title: "Kennesaw State University",
      description: "2nd Year PhD Student",
      imgUrl: projImg1,
    },
    {
      title: "Al Akhawayn University",
      description: "Bachelor of Science in Computer Science",
      imgUrl: projImg2,
    },
  ];

  const professionalExperiences = [
    {
      title: "Startup",
      description: "Lead Developer",
      imgUrl: projImg8,
    },
    {
      title: "Tech Company",
      description: "Software Engineer",
      imgUrl: projImg3,
    },
    {
      title: "Startup",
      description: "Lead Developer",
      imgUrl: projImg4,
    },
    {
      title: "Startup",
      description: "Lead Developer",
      imgUrl: projImg5,
    },
    {
      title: "Startup",
      description: "Lead Developer",
      imgUrl: projImg6,
    },
    {
      title: "Startup",
      description: "Lead Developer",
      imgUrl: projImg7,
    },
  ];

  const chunkArray = (array, chunkSize) => {
    const results = [];
    for (let i = 0; i < array.length; i += chunkSize) {
      results.push(array.slice(i, i + chunkSize));
    }
    return results;
  };

  const professionalChunks = chunkArray(professionalExperiences, 3);

  return (
    <section className="project" id="background">
      <Container>
        <Row>
          <Col size={12}>
            <TrackVisibility>
              {({ isVisible }) =>
              <div className={isVisible ? "animate__animated animate__fadeIn" : ""}>
                <h2>Background</h2>
                <p>A committed team player who values the gratification of success as well as the challenging journey to it. Nevertheless, I do enjoy improving and acquiring skills that are thought to be vital in the industry my work revolves around.</p>
                <Carousel>
                  {professionalChunks.map((chunk, index) => (
                    <Carousel.Item key={index}>
                      <Row>
                        {chunk.map((experience, subIndex) => (
                          <Col key={subIndex} md={4}>
                            <ProjectCard {...experience} />
                          </Col>
                        ))}
                      </Row>
                    </Carousel.Item>
                  ))}
                </Carousel>
              </div>}
            </TrackVisibility>
          </Col>
        </Row>
      </Container>
      <img className="background-image-right" src={colorSharp2} alt="background"></img>
    </section>
  );
}
