import React, { useState } from 'react';
import colorSharp from "../assets/img/color-sharp.png";

export const Skills = () => {
  const [tooltip, setTooltip] = useState({ visible: false, content: "", x: 0, y: 0 });

  const skills = [
    { title: "Python Scripting", details: "Experience with scripting, automation, and task automation using Python." },
    { title: "ML Frameworks", details: "Proficient in various machine learning frameworks like TensorFlow, PyTorch, and scikit-learn." },
    { title: "Databases", details: "Skilled in designing, managing, and querying relational and NoSQL databases." },
    { title: "Data Analysis", details: "Expertise in analyzing complex data sets, using statistical tools and data visualization techniques." },
    { title: "DevOps", details: "Experience in CI/CD pipelines, cloud services, and infrastructure automation." }
  ];

  const showTooltip = (e, content) => {
    setTooltip({ visible: true, content, x: e.clientX, y: e.clientY });
  };

  const hideTooltip = () => {
    setTooltip({ ...tooltip, visible: false });
  };

  return (
    <section className="skill" id="skills">
        <div className="container">
            <div className="row">
                <div className="col-12">
                    <div className="skill-bx wow zoomIn">
                        <h2>Skills</h2>
                        <p>Acquired skills through academic and professional training include:</p>
                        <div className="radar-chart">
                            {/* Web structure */}
                            <svg width="400" height="400" viewBox="0 0 400 400">
                                <circle cx="200" cy="200" r="10" fill="#fff" /> {/* Center */}
                                {skills.map((skill, index) => {
                                    const angle = (index / skills.length) * 2 * Math.PI;
                                    const x = 200 + 150 * Math.cos(angle); // Adjust radius as needed
                                    const y = 200 + 150 * Math.sin(angle);
                                    return (
                                        <g key={index}>
                                            {/* Line from center to each skill point */}
                                            <line x1="200" y1="200" x2={x} y2={y} stroke="#fff" />
                                            {/* Skill label */}
                                            <text
                                                x={x}
                                                y={y}
                                                fill="#fff"
                                                onMouseEnter={(e) => showTooltip(e, skill.details)}
                                                onMouseLeave={hideTooltip}
                                                style={{ cursor: 'pointer', fontSize: '14px' }}
                                            >
                                                {skill.title}
                                            </text>
                                        </g>
                                    );
                                })}
                                {/* Web structure with skill level indicators (can be customized) */}
                                <circle cx="200" cy="200" r="50" stroke="#fff" fill="none" />
                                <circle cx="200" cy="200" r="100" stroke="#fff" fill="none" />
                                <circle cx="200" cy="200" r="150" stroke="#fff" fill="none" />
                            </svg>
                        </div>
                        {tooltip.visible && (
                            <div
                              style={{
                                position: 'fixed',
                                top: tooltip.y + 10,
                                left: tooltip.x + 10,
                                backgroundColor: 'rgba(0, 0, 0, 0.85)',
                                color: '#fff',
                                padding: '8px 12px',
                                borderRadius: '5px',
                                boxShadow: '0px 4px 12px rgba(0, 0, 0, 0.1)',
                                maxWidth: '200px',
                                lineHeight: '1.5em',
                                zIndex: 1000,
                                whiteSpace: 'normal',
                                wordWrap: 'break-word',
                                opacity: 1,
                                pointerEvents: 'none'
                              }}
                            >
                              {tooltip.content}
                            </div>
                        )}
                    </div>
                </div>
            </div>
        </div>
        <img className="background-image-left" src={colorSharp} alt="Background"/>
    </section>
  );
}
