import React from 'react';
import { Radar } from 'react-chartjs-2';

export const Skills = () => {
  const data = {
    labels: ['Python Scripting', 'ML Frameworks', 'Databases', 'Data Analysis', 'DevOps'],
    datasets: [
      {
        label: 'Proficiency',
        data: [90, 85, 80, 75, 70],
        backgroundColor: 'rgba(34, 202, 236, 0.2)', // Light blue background for the radar chart
        borderColor: 'rgba(34, 202, 236, 1)', // Blue border for the radar chart
        pointBackgroundColor: 'rgba(34, 202, 236, 1)', // Blue points on the radar chart
        pointBorderColor: '#fff', // White border for the points
        pointHoverBackgroundColor: '#fff', // White background for points on hover
        pointHoverBorderColor: 'rgba(34, 202, 236, 1)', // Blue border on hover
      },
    ],
  };

  const options = {
    scale: {
      ticks: {
        beginAtZero: true,
        min: 0,
        max: 100,
        stepSize: 20,
        backdropColor: 'rgba(0, 0, 0, 0)', // Transparent backdrop for the ticks
        fontColor: '#fff', // White text for the numbers on the scales
      },
      angleLines: {
        color: '#fff', // White lines radiating from the center
      },
      gridLines: {
        color: '#fff', // White grid lines
      },
      pointLabels: {
        fontColor: '#fff', // White labels for the skills
      },
    },
    elements: {
      line: {
        tension: 0.2, // Smoothness of the radar chart lines
      },
    },
  };

  return (
    <section className="skill" id="skills" style={{ backgroundColor: '#111', padding: '50px 0', textAlign: 'center' }}>
      <h2 style={{ marginBottom: '40px' }}>Skills</h2>
      <Radar data={data} options={options} />
    </section>
  );
};
