import React from 'react';
import { Radar } from 'react-chartjs-2';
import { Container } from "react-bootstrap";

export const Skills = () => {
  const data = {
    labels: ['Python Scripting', 'ML Frameworks', 'Databases', 'Data Analysis', 'DevOps'],
    datasets: [
      {
        label: 'Proficiency',
        data: [90, 85, 80, 75, 70],
        backgroundColor: 'rgba(34, 202, 236, 0.2)',
        borderColor: 'rgba(34, 202, 236, 1)',
        pointBackgroundColor: 'rgba(34, 202, 236, 1)',
        pointBorderColor: '#fff',
        pointHoverBackgroundColor: '#fff',
        pointHoverBorderColor: 'rgba(34, 202, 236, 1)',
      },
    ],
  };

  const options = {
    scale: {
      ticks: {
        beginAtZero: true,
        min: 0,
        max: 100,
        stepSize: 20,
        backdropColor: 'rgba(0, 0, 0, 0)', // Transparent backdrop for the ticks
        fontColor: '#fff', // White text for the numbers on the scales
      },
      angleLines: {
        color: '#fff', // White lines radiating from the center
      },
      gridLines: {
        color: '#fff', // White grid lines
      },
      pointLabels: {
        fontColor: '#fff', // White labels for the skills
      },
    },
    elements: {
      line: {
        tension: 0.2, // Smoothness of the radar chart lines
      },
    },
  };

  return (
    <section className="skill" id="skills">
      <Container className="skills-web" style={{ color: '#fff', paddingTop: '20px' }}>
        <h2>Skills</h2>
        <Radar data={data} options={options} />
      </Container>
    </section>
  );
};
